import axios from "axios";

const LIVRO_API_URL = "http://localhost:3000/Passageiro";

class PassageiroService {
  getAllPassageiro() {
    return axios.get(PASSAGEIRO_API_URL);
  }

  createPassageiro(livro) {
    return axios.post(PASSAGEIRO_API_URL, livro);
  }

  getPassageiroById(livroId) {
    return axios.get(PASSAGEIRO_API_URL + "/" + livroId);
  }

  updatePassageiro(livroId, livro) {
    return axios.put(PASSAGEIRO_API_URL + "/" + livroId, livro);
  }

  deleteLivro(livroId) {
    return axios.delete(PASSAGEIRO_API_URL + "/" + livroId);
  }
}

export default new LivroService();
